property.code:
document.observe('xwiki:dom:loaded', function() {
  var groupsTable = $('groups-directory');
  if (!groupsTable) {
    return false;
  }
  groupsTable.select('tr .tools a.tool.delete').invoke('observe', 'click', function(clickEvent) {
    clickEvent.stop();
    var item = clickEvent.findElement('a');
    item.blur();
    if (item.disabled) {
      // Do nothing if the button was already clicked and it's waiting for a response from the server.
      return;
    } else {
      var url = item.href + "?confirm=1&form_token=" + $$('meta[name=form_token]')[0].content + (Prototype.Browser.Opera ? "" : "&ajax=1");
      new XWiki.widgets.ConfirmedAjaxRequest(
        /* Ajax request URL */
        url,
        /* Ajax request parameters */
        {
          onCreate : function() {
            // Disable the button, to avoid a cascade of clicks from impatient users
            item.disabled = true;
          },
          onSuccess : function() {
            // Go to space WebHome
            // window.location = new XWiki.Document('WebHome', XWiki.Document.currentSpace).getURL('view');
            item.up('tr').remove();
          },
          onFailure : function() {
            // Better luck next time!
            item.disabled = false;
          }
        },
        /* Interaction parameters */
        {
          confirmationText: "Are you sure you wish to delete this group?"
        }
      );
    }
  });

  var creationForm = $('group-creation-form');
  if (creationForm) {
    creationForm.observe('submit', function(event) {
      var iName = creationForm.down('input[name="name"]');
      if (iName.value.strip() == '') {
        event.stop();
        iName.focus();
      }
    });
  }
});

